mapper.select:
select grade 
from member 
where id = #{id} and pw = #{pw}

select * 
from member 
where id=#{id} and pw=#{pw}

mapper.insert:
Insert into member(
id, pw, nick, mobile, gender, age
)	
VALUES
(
#{id}, #{pw}, #{nick}, #{mobile}, #{gender}, #{age}
)

mapper.select:
UPDATE member
